import { useState, useEffect } from 'react';

const Questao1A = () => { 
    const lista = [
        { a: 10, b: 3, c: 7 },
        { a: 5, b: -3, c: 9 },
        { a: 1, b: 9, c: 40 }
    ];

    return (
        <div>
            <Questao1B lista={lista} />
        </div>
    );
}

function Questao1B({ lista }) {
    const [maioresValores, setMaioresValores] = useState([]);

    useEffect(() => {
        const novosMaioresValores = [];

        for (let i = 0; i < lista.length; i++) {
            const item = lista[i];
            const maiorValor = Math.max(item.a, item.b, item.c);
            novosMaioresValores.push(maiorValor);
        }

        setMaioresValores(novosMaioresValores);
    }, [lista]);

    return (
        <ul>
            {maioresValores.map((valor, index) => (
                <li key={index}>Maior valor do item {index + 1}: {valor}</li>
            ))}
        </ul>
    );
}

export default Questao1A;
