import { useState, useEffect } from 'react';

const Questao1A = () => { 
    const lista = [
        { a: 10, b: 3, c: 7 },
        { a: 5, b: -3, c: 9 },
        { a: 1, b: 9, c: 40 }
    ];

    return (
        <div>
            <Questao1B lista={lista} />
        </div>
    );
}

function Questao1B({ lista }) {
    const [maioresElementos, setMaioresElementos] = useState([]);

    useEffect(() => {
        const novosMaioresElementos = [];

        for (let i = 0; i < lista.length; i++) {
            const item = lista[i];
            const maiorElemento = Math.max(item.a, item.b, item.c);
            novosMaioresElementos.push(maiorElemento);
        }
        setMaioresElementos(novosMaioresElemetnos);
        }
        , 
        [lista]
    );

    // Gera os elementos da lista sem usar o map
    const listaElementos = [];
    for (let i = 0; i < maioresElementos.length; i++) {
        listaElementos.push(
            <li key={i}>Maior valor do item {i + 1}: {maioresElementos[i]}</li>
        );
    }

    return (
        <ul>
            {listaElementos}
        </ul>
    );
}

export default Questao1A;
